const o={btn:document.querySelector(".go-top"),show(){this.btn.classList.add("show")},hide(){this.btn.classList.remove("show")},addEventListener(){window.addEventListener("scroll",()=>{(window.scrollY||document.documentElement.scrollTop)>400?this.show():this.hide()}),document.querySelector(".go-top").onclick=()=>{window.scrollTo({top:0,left:0,behavior:"smooth"})}}};o.addEventListener();
